mapper.select:
select u.*,o.sub_exam_nm as par_sub_exam_nm ,s.sub_nm,e.name from (select a1.* from xgt_sub_exam a1 , (select b1.sub_exam_id from xgt_sub_exam b1 where b1.sub_exam_id = #{id} UNION select b1.sub_exam_id from xgt_sub_exam b1 where b1.par_sub_exam_id = #{id} union select b1.sub_exam_id from xgt_sub_exam b1,xgt_sub_exam b2 where b1.par_sub_exam_id=b2.sub_exam_id and b2.par_sub_exam_id = #{id} union select b1.sub_exam_id from xgt_sub_exam b1,xgt_sub_exam b2, xgt_sub_exam b3 where b1.par_sub_exam_id=b2.sub_exam_id and b2.par_sub_exam_id=b3.sub_exam_id and b3.par_sub_exam_id = #{id} ) m1 where a1.sub_exam_id=m1.sub_exam_id
and (sub_exam_nm like CONCAT('%',#{condition},'%') or sub_exam_no like CONCAT('%',#{condition},'%') or 1<1)
)u LEFT JOIN xgt_sub_exam o on u.par_sub_exam_id = o.sub_exam_id LEFT JOIN xgt_subject s on u.sub_id =s.sub_id LEFT JOIN user e on u.id =e.id limit #{start},#{rows}

        	    select u.*,o.sub_exam_nm as par_sub_exam_nm,s.sub_nm,e.name from
        		(select	a1.*
                                		from xgt_sub_exam a1 ,
                                		(select b1.sub_exam_id from xgt_sub_exam b1 where b1.sub_exam_id = #{id}
                                		UNION
                                		select b1.sub_exam_id from xgt_sub_exam b1 where b1.par_sub_exam_id = #{id}
                                		union
                                		select b1.sub_exam_id from xgt_sub_exam b1,xgt_sub_exam b2 where b1.par_sub_exam_id=b2.sub_exam_id and b2.par_sub_exam_id = #{id}
                                		union
                                		select b1.sub_exam_id from xgt_sub_exam b1,xgt_sub_exam b2, xgt_sub_exam b3 where b1.par_sub_exam_id=b2.sub_exam_id and b2.par_sub_exam_id=b3.sub_exam_id and b3.par_sub_exam_id =  #{id}
                                		) m1
                                		where a1.sub_exam_id=m1.sub_exam_id

                                                    and (sub_exam_nm like CONCAT('%',#{condition},'%') or sub_exam_no like CONCAT('%',#{condition},'%') or 1<1)

                                        )u
                                        LEFT JOIN xgt_sub_exam o
                                        				on u.par_sub_exam_id = o.sub_exam_id
				LEFT JOIN xgt_subject s on u.sub_id =s.sub_id
				LEFT JOIN user  e on u.id =e.id
